INSERT INTO `school` VALUES ('603', '河北建筑工程学院', '河北省', '55万', '0', '13', '1950', '810', '', '普通本科', '理工类', '普通本科', '公办', '', '{}', '河北', '张家口市', '', 'jyxsczb@126.com', '河北省张家口市朝阳西大街13号', '075024', 'http://zs.hebiace.edu.cn/', '0313-4187721', '', '河北建筑工程学院坐落在被誉为“第二延安”和“长城博物馆”美称的冬奥名城张家口市，学校办学历史可追溯到1950年成立的察哈尔张家口市技术学校，1978年升格为本科高校“河北建筑工程学院”，是国务院批准的首批具有学士学位授予权院校，2008年在教育部本科教学工作水平评估中获得“优秀”，2013年批准为硕士学位授予单位，是河北省唯一以培养土木建筑类专业人才为', '3', '0', '0', '0', 458, '418', '672', '0', '0', '93.93', '8.36', '0.00', '569', '1407', '71.00', '29.00', '2', '2');
