INSERT INTO `school` VALUES ('518', '遵义医科大学', '贵州省', '161万', '0', '52', '1947', '2515', '大连医学院,遵义医学院', '普通本科', '医药类', '普通本科', '公办', '', '{C: 2, C-: 5}', '贵州', '遵义市', '红花岗区', 'zyyxyzsbgs@163.com', '新蒲校区：贵州省遵义市新蒲新区学府西路6号,大连路校区：贵州省遵义市汇川区大连路201号,珠海校区：广东省珠海市金湾区金海岸', '563000', 'https://zyzb.zmu.edu.cn/', '0851-28205801,0851-28204791', '0851-28642666', '遵义医科大学创建于1947年，是抗日战争胜利后中国共产党创办的第一所医学本科院校，曾用关东医学院、大连大学医学院、大连医学院等名称。1969年为支援“三线”建设，学校整体搬迁至贵州省遵义市，更名为遵义医学院。2018年更名为遵义医科大学，2021年获批博士学位授予单位。经过70余年的建设与发展，已成为以医学为主、多学科支撑、办学特色鲜明、办学实力雄厚的省', '8', '0', '1', '0', 0, '301', '297', '0', '0', '83.14', '15.20', '0.17', '2233', '1248', '35.85', '64.15', '2', '2');
