INSERT INTO `school` VALUES ('511', '海南师范大学', '海南省', '183万', '0', '46', '1949', '3100', '海南师范学院', '普通本科', '师范类', '普通本科', '公办', '', '{C+: 2}', '海南', '海口市', '', 'hszsb@hainnu.edu.cn', '南校区：海南省海口市琼山区龙昆南路99号,桂林洋校区：海南省海口市美兰区校际一号路3号', '571158', 'http://zhaosheng.hainnu.edu.cn/', '0898-65888710,0898-65880571', '', '海南师范大学位于美丽的滨海旅游城市、国家历史文化名城——海口市，现有龙昆南、桂林洋两个校区，占地面积约3100亩，海南省重点大学，是海南省卓越师资、高级专门人才重要培养基地和科学研究基地。建校至今，已为社会培养了18万余名基础教育师资和各类高级人才，被誉为“琼岛名校，教师摇篮”。办学历史悠久。学校前身可追溯到有300多年历史的琼台书院，1949年秋', '19', '5', '0', '0', 235, '209', '386', '0', '0', '93.75', '7.04', '1.53', '3413', '1387', '28.90', '71.10', '2', '2');
