INSERT INTO `school` VALUES ('508', '内蒙古工业大学', '内蒙古自治区', '0', '1', '15', '1951', '3228', '绥远省高级工业学校', '普通本科', '理工类', '普通本科', '公办', '', '{B-: 2, C: 2, C-: 7}', '内蒙古', '呼和浩特市', '新城区', 'zsb@imut.edu.cn', '新城校区：内蒙古自治区呼和浩特市新城区爱民街49号,金川校区：内蒙古自治区呼和浩特市经济技术开发区金川工业园区', '010051', 'http://zsb.imut.edu.cn/', '0471-6576346', '', '内蒙古工业大学坐落在内蒙古自治区呼和浩特市，前身是始建于1951年的绥远省高级工业学校。1958年在清华大学等支援下成立内蒙古工学院，曾隶属机械工业部、农业机械部，1983年划归内蒙古自治区，1993年更名为内蒙古工业大学。经过70年的发展，学校已建设成为一所以工为主，工、理、文、经、法、管、农、艺术相结合，具有博士、硕士、本科完整人才培养体系的特色鲜明', '25', '5', '0', '0', 308, '237', '396', '0', '0', '79.05', '8.69', '0.38', '', '', '', '', '2', '2');
