INSERT INTO `school` VALUES ('488', '兰州交通大学', '甘肃省', '222万', '1', '62', '1958', '1564', '兰州铁道学院', '普通本科', '理工类', '普通本科', '公办', '', '{B: 7, C-: 4}', '甘肃', '兰州市', '', 'zsb@mail.lzjtu.cn', '校本部：甘肃省兰州市安宁区安宁西路88号,铁道校区：甘肃省兰州市城关区平凉路166号', '730070', 'http://zsb.lzjtu.edu.cn/', '0931-4938051,0931-4956179', '', '兰州交通大学坐落在甘肃省省会城市、“一带一路”重要节点城市兰州，是中国国家铁路集团有限公司与甘肃省人民政府、国家铁路局与甘肃省人民政府双共建高校，西班牙塞维利亚大学孔子学院授权建设单位，教育部对口支援高校，教育部“卓越工程师教育培养计划”入选高校，教育部“深化创新创业教育改革示范高校”，教育部“中西部教育振兴计划”支持高校，教育部', '25', '6', '0', '29', 237, '158', '246', '0', '1716', '97.12', '10.49', '1.01', '1593', '4067', '71.86', '28.14', '2', '2');
INSERT INTO `school` VALUES ('487', '南阳师范学院', '河南省', '139万', '0', '41', '1907', '2190', '', '普通本科', '师范类', '普通本科', '公办', '', '{}', '河南', '南阳市', '', 'nynuzsb@163.com', '河南省南阳市卧龙路1638号', '473061', 'http://www2.nynu.edu.cn/xzbm/zhaosheng/', '0377-63513466,0377-63516507', '', '南阳师范学院为河南省属普通本科高校，始建于1951年的河南省立南阳师范学校，1958年升格为南阳师范专科学校，2000年升格为南阳师范学院。2007年通过教育部本科教学工作水平评估，2011年获批为“服务国家特殊需求人才培养项目——学士学位授予单位开展培养硕士专业学位研究生试点”单位，2016年被确定为河南省示范性应用技术类型本科院校，2017年获批为硕士学位授予单位', '0', '0', '0', '0', 356, '346', '382', '0', '0', '92.38', '11.74', '0.67', '5098', '2151', '29.71', '70.29', '2', '2');
INSERT INTO `school` VALUES ('485', '周口师范学院', '河南省', '145万', '0', '41', '1973', '1600', '', '普通本科', '师范类', '普通本科', '公办', '', '{}', '河南', '周口市', '川汇区', 'zhaoban@zknu.edu.cn', '河南省周口市川汇区文昌路东段', '466001', 'http://zs.zknu.edu.cn/', '0394-8592469', '', '周口师范学院是河南省属普通本科高校，地处历史文化名城周口市。周口素有“华夏先驱，九州圣迹”之誉，太昊伏羲氏在此建都，古代思想家老子诞生于此，孔子在此问道游学，谢安、周兴嗣、程颢、袁世凯等都是周口历史文化名人。学校交通便利，郑合（郑州至合肥）高铁，大广（大庆至广州）、宁洛（南京至洛阳）等高速公路，漯阜（漯河至阜阳）铁路直达学校；学校', '0', '0', '0', '0', 504, '398', '465', '0', '0', '94.62', '11.75', '0.12', '4322', '2123', '32.94', '67.06', '2', '2');
INSERT INTO `school` VALUES ('484', '洛阳师范学院', '河南省', '225万', '0', '41', '1916', '2850', '', '普通本科', '师范类', '普通本科', '公办', '', '{}', '河南', '洛阳市', '洛龙区', 'lszsk2018@163.com', '河南省伊滨区吉庆路6号', '471934', 'http://sites.lynu.edu.cn/zsxx/', '0379-68618104', '', '洛阳师范学院是一所省属普通高等本科院校，位于千年帝都、牡丹花城、丝路起点——洛阳。学校地处伊水之滨，万安山下，东汉太学便发端于此。南望二程故里，传颂着程门立雪、鲁台望道的佳话；西望关林和世界文化遗产龙门石窟，绽放着世界文化遗产的璀璨光芒。学校前身是始建于1916年的河洛师范学校，历经河南省立河洛师范学校、河南省立第四师范学校、河南省立洛', '0', '0', '0', '0', 367, '340', '361', '0', '0', '73.18', '21.66', '0.80', '4228', '1652', '28.10', '71.90', '2', '2');
INSERT INTO `school` VALUES ('483', '兰州理工大学', '甘肃省', '195万', '10', '62', '1919', '2430', '甘肃省立工艺学校', '普通本科', '理工类', '普通本科', '公办', '', '{B: 4, B-: 2, C: 2, C+: 2}', '甘肃', '兰州市', '', 'gdzb@lut.cn', '主校区：甘肃省兰州市七里河区兰工坪路287号,西校区：甘肃省兰州市七里河区彭家坪路36号', '730050', '', '0931-2973947,0931-2755263', '0931-2973715', '兰州理工大学坐落于甘肃省省会兰州市，是甘肃省人民政府、教育部、国家国防科技工业局共建高校，甘肃省首批高水平大学建设高校。中西部高校基础能力建设工程、国家大学生创新型实验计划、教育部卓越工程师计划入选高校，国家国防教育特色学校。学校前身是1919年创立的甘肃省立工艺学校；1958年，在组建兰州工学院的基础上，将甘肃交通大学并入，定名为甘肃工业大', '23', '6', '0', '0', 212, '178', '189', '0', '1637', '97.23', '13.70', '0.70', '1325', '4169', '75.88', '24.12', '2', '2');
INSERT INTO `school` VALUES ('482', '新乡医学院', '河南省', '115万', '0', '41', '1896', '1745', '', '普通本科', '医药类', '普通本科', '公办', '', '{C: 5, C-: 2}', '河南', '新乡市', '红旗区', '', '河南省新乡市红旗区金穗大道601号', '453003', 'http://zjc.xxmu.edu.cn/zsxxw/', '0373-3029485', '', '新乡医学院位于河南省新乡市，是河南省唯一一所独立建制的西医本科院校。学校渊源可追溯到1896年医学博士罗维灵在古城卫辉开办的西医诊所，医学教育肇始于1922年惠民医院护士学校，招收四年制护理专业学生并由中华护理学会（1923年名为“中华护士会”）颁发文凭。1949年冀鲁豫卫生学校及哈励逊医院迁入，于1950年成立平原省医科学校。学校经历华北第二医士学校、河南', '12', '0', '0', '0', 0, '334', '239', '0', '1737', '96.43', '20.48', '0.22', '2997', '1120', '27.20', '72.80', '2', '2');
INSERT INTO `school` VALUES ('481', '中原工学院', '河南省', '415万', '1', '41', '1955', '1610', '', '普通本科', '理工类', '普通本科', '公办', '', '{C: 4}', '河南', '郑州市', '中原区', 'zsc@zut.edu.cn', '河南省郑州市中原中路41号', '450007', 'https://zsc.zut.edu.cn', '0371-67698700', '0371-62506888', '中原工学院是一所以工为主，以电子信息和纺织服装为特色优势，工、管、艺、理、文、经、法、哲等多学科协调发展的河南省特色骨干学科建设高校。学校始建于1955年，原名郑州纺织工学院，隶属于原国家纺织工业部，1998年划转河南省管理，2000年更名为中原工学院。学校设有纺织学院、能源与环境学院、机电学院、服装学院、艺术设计学院、电子信息学院等21个教学院（部', '13', '0', '0', '1', 241, '287', '400', '0', '0', '95.10', '18.40', '1.68', '2559', '3610', '58.52', '41.48', '2', '2');
