INSERT INTO `school` VALUES ('476', '闽南师范大学', '福建省', '220万', '0', '35', '1958', '2000', '', '普通本科', '师范类', '普通本科', '公办', '', '{}', '福建', '漳州市', '', 'zzsyzsb@163.com', '福建省漳州市芗城区县前直街36号', '363000', 'http://zsb.mnnu.edu.cn/', '0596-2529991', '', '闽南师范大学是福建省重点建设高校、福建省“双一流”建设高校，成立于1958年春。1963年，在周恩来总理亲自倡导和关怀下，国务院批准成立福建第二师范学院，由此开启我校本科教育之扉。1986年经原国家教委批准复办福建第二师范学院，并更名为漳州师范学院。1992年获得学士学位授予权。2000年顺利通过教育部首批师范院校本科教学工作合格评估。2003年被国务院学位委员', '7', '1', '0', '0', 319, '330', '399', '0', '0', '96.64', '6.85', '1.53', '2616', '1239', '32.14', '67.86', '2', '2');
INSERT INTO `school` VALUES ('475', '泉州师范学院', '福建省', '96万', '9', '35', '1958', '1815', '', '普通本科', '师范类', '普通本科', '公办', '', '{}', '福建', '泉州市', '', 'zhaosheng@qztc.edu.cn', '泉州主校区：福建省泉州市丰泽区东海大街398号,南安校区：福建省泉州市南安市码头镇仙美街100号', '362000', 'http://www.qztc.edu.cn/zs', '0595-22919911', '0595-22796091', '泉州师范学院坐落于福建省三大中心城市之一-泉州市。泉州是首批中国历史文化名城、首届东亚文化之都、古代海上丝绸之路起点，经济总量连续22年位居福建省首位。学校于2000年经教育部批准建立，系福建省第一所新建地方性省属本科高校，由创办于1923年的泉州师范学校、1939年的南安师范学校、1951年的泉州教育学院和1958年的泉州师范高等专科学校合并组建。1999年和2001年', '0', '0', '7', '0', 354, '353', '497', '0', '0', '97.18', '2.75', '0.50', '2899', '1763', '37.82', '62.18', '2', '2');
