INSERT INTO `school` VALUES ('463', '河南理工大学', '河南省', '580万', '1', '41', '1909', '4100', '焦作工学院', '普通本科', '理工类', '普通本科', '公办', '', '{A-: 1, B: 2, B-: 2, C: 4, C+: 6, C-: 12}', '河南', '焦作市', '', 'zsjy@hpu.edu.cn', '南校区：河南省焦作高新区世纪大道2001号,北校区：河南省焦作市解放中路142号,西校区：河南省焦作市建设西路55号', '454000', 'http://www6.hpu.edu.cn/web5/', '0391-3986000,0391-3986111,0391-3987225', '', '1909年，河南理工大学（简称“河南理工”；英文：HenanPolytechnicUniversity，英文简称“HPU”）的前身——焦作路矿学堂，在黄河之滨、太行之阳的焦作诞生，成为我国第一所矿业高等学府和河南省建立最早的高等学校。学校历经福中矿务大学、私立焦作工学院、国立西北工学院、国立焦作工学院、焦作矿业学院（简称“焦作矿院”；英文：JiaozuoMiningInstitute，英文简称“JMI”）和', '21', '6', '0', '0', 176, '124', '151', '0', '1597', '94.65', '21.44', '0.54', '2481', '5065', '67.12', '32.88', '2', '2');
