INSERT INTO `school` VALUES ('385', '湘潭大学', '湖南省', '291万', '3', '43', '1958', '3200', '', '普通本科', '综合类', '普通本科', '公办', '双一流', '{B: 3, B+: 5, B-: 4, C: 7, C+: 11}', '湖南', '湘潭市', '', 'zhaosheng@xtu.edu.cn', '湖南省湘潭市雨湖区北二环', '411105', 'https://www.xtu.edu.cn/zsjy/bkszs.htm', '0731-58293807,0731-58292814,0731-58292841', '', '湘潭大学简称“湘大”，是一代伟人毛泽东同志亲自倡办的综合性全国重点大学、国家“双一流”建设高校。学校创办于1958年，同年9月10日，毛泽东同志亲笔题写“湘潭大学”校名，并亲切嘱托“一定要把湘潭大学办好”。1974年，邓小平、李先念等中央领导同志批准湘潭大学复校。1978年，学校被国务院确定为全国16所文理工综合性重点大学之一。1981年，学校成为全国首批硕', '31', '16', '4', '0', 106, '98', '113', '0', '1024', '92.99', '23.02', '1.09', '3057', '2651', '46.44', '53.56', '2', '2');
