INSERT INTO `school` VALUES ('381', '长春工业大学', '吉林省', '126万', '3', '22', '1952', '1817', '', '普通本科', '理工类', '普通本科', '公办', '', '{B-: 3, C: 3}', '吉林', '长春市', '', '', '吉林省长春市延安大街2055号', '130012', 'https://bzkzs.ccut.edu.cn/', '0431-85914753', '', '长春工业大学是一所以工为主，工、管、文、理、经、法、艺术等多学科相互支撑、协调发展的省属重点大学。始建于1952年，建校初期是国家为筹建中国第一汽车制造厂而创办的长春汽车工业学校，先后经历了长春工业专科学校、吉林理工学院、吉林工学院等发展阶段。1970年6月吉林工学院与吉林工业大学合并，1978年10月复校。2000年9月，长春煤炭工业学校、长春煤炭管理干部', '20', '5', '0', '0', 288, '240', '405', '0', '0', '84.46', '13.33', '1.73', '1628', '2927', '64.26', '35.74', '2', '2');
