INSERT INTO `school` VALUES ('379', '长春理工大学', '吉林省', '212万', '1', '22', '1958', '1077', '长春光学精密机械学院', '普通本科', '理工类', '普通本科', '公办', '', '{B: 2, B-: 1, C: 1, C+: 4, C-: 5}', '吉林', '长春市', '', 'zsb@cust.edu.cn', '吉林省长春市卫星路7089号', '130022', 'http://zsb.cust.edu.cn/', '0431-85582999', '86-431-85583888', '长春理工大学原名长春光学精密机械学院，1958年由中国科学院创办，是新中国第一所培养光学专门人才的高等院校。经过60多年的建设与发展，学校现已成为一所具有鲜明光电特色和国防特色的吉林省省属重点大学，是吉林省、国家国防科技工业局、长春市共建院校，享有“中国光学英才摇篮”的美誉。因光而生，与国家教育事业发展同频共振。“两弹一星”元勋、两院院士', '20', '9', '1', '8', 142, '158', '250', '0', '1742', '90.31', '19.50', '2.30', '1948', '2727', '58.33', '41.67', '2', '2');
INSERT INTO `school` VALUES ('378', '西安体育学院', '陕西省', '30万', '0', '61', '1954', '1300', '', '普通本科', '体育类', '普通本科', '公办', '', '{B: 5}', '陕西', '西安市', '', 'zsb@xaipe.edu.cn', '陕西省西安市含光北路65号', '710068', 'http://www.xaipe.edu.cn/zsbgs/', '029-88409422', '', '西安体育学院创办于1954年，原名西北体育学院，1956年更名为西安体育学院，是新中国最早建立的六所体育高等院校之一，原直属国家体育总局（国家体委），2001年实行中央与地方共建，是国家体育总局和陕西省人民政府共建院校，陕西省“国内一流学科”建设高校，陕西省拟新增博士学位授权单位立项建设单位。学校现有校本部、沣峪、鄠邑三个校区，占地面积1300余亩。目', '2', '0', '0', '0', 0, '398', '692', '0', '0', '86.70', '4.24', '0.43', '611', '1464', '70.55', '29.45', '2', '2');
INSERT INTO `school` VALUES ('377', '长春大学', '吉林省', '0', '0', '22', '1949', '1530', '', '普通本科', '综合类', '普通本科', '公办', '', '{}', '吉林', '长春市', '九台区', 'zsb@ccu.edu.cn', '吉林省长春市卫星路6543号', '130022', 'http://zsb.ccu.edu.cn', '0431-85250087,13604367767', '', '长春大学坐落在素有“北国春城”之称的吉林省长春市，是一所底蕴厚重、特色鲜明的省属综合性大学。学校前身是1949年由原东北人民政府工业部创办的长春工业会计统计专门学校，为新中国培养了第一批工业会计统计人才。在同步国家发展与时代变革中学校数次易名，1983年更名为吉林机电专科学校。1987年经原国家教育委员会批准，原吉林科技大学、吉林机电专科学校、长', '6', '0', '0', '8', 365, '301', '571', '0', '0', '92.21', '6.96', '2.70', '1854', '1828', '50.00', '50.00', '2', '2');
INSERT INTO `school` VALUES ('376', '西北政法大学', '陕西省', '0', '0', '61', '1937', '1347', '', '普通本科', '政法类', '普通本科', '公办', '', '{C: 1}', '陕西', '西安市', '', 'xfdzjc@126.com', '长安校区：陕西省西安市西长安街558号,雁塔校区：陕西省西安市长安南路300号', '710122', 'https://bkzn.nwupl.edu.cn/', '029-85388805,029-88182275', '', '西北政法大学是一所法学特色鲜明，哲学、经济学、管理学、文学等学科相互支撑、协调发展的多科性大学。学校是国家法治人才培养基地，是被誉为政法人才培养国家队的“五院四系”之一，是西北地区法学教育、法学研究中心和人文社会科学研究的重要基地，是陕西省重点建设的高水平大学、一流学科建设高校，是全国法学高等教育“立格联盟”和西安高水平有特色高校', '5', '0', '0', '0', 0, '192', '330', '0', '0', '84.67', '11.15', '2.79', '2454', '1447', '37.09', '62.91', '2', '2');
