INSERT INTO `school` VALUES ('361', '西安工业大学', '陕西省', '177万', '0', '61', '1955', '1200', '', '普通本科', '理工类', '普通本科', '公办', '', '{C: 1, C-: 3}', '陕西', '西安市', '未央区', 'zhshb@xatu.edu.cn', '未央校区：陕西省西安市未央大学园区学府中路2号,金花校区：陕西省西安市碑林区金花北路4号', '710021', 'https://zsb.xatu.edu.cn/', '029-86173115', '029-86173399', '西安工业大学创建于1955年，是国家“一五”计划156个重点建设项目的军工配套项目，具有鲜明的军工特色，是兵器行业部署在西北地区唯一的本科院校。65年来，学校践行“敦德励学，知行相长”的校训，弘扬“忠诚进取，精工博艺”的校风，注重学生军工品格的锤炼，强化工程实践能力的培养，为区域经济和国防工业输送了14万余名高级专门人才。学校占地面积1200亩，校舍', '16', '3', '0', '0', 275, '194', '317', '0', '0', '93.02', '10.30', '0.69', '1998', '2526', '55.84', '44.16', '2', '2');
