INSERT INTO `school` VALUES ('352', '西安科技大学', '陕西省', '250万', '5', '61', '1958', '1380', '西安科技学院，西安矿业学院', '普通本科', '理工类', '普通本科', '公办', '', '{A-: 1, B-: 2, C+: 7, C-: 4}', '陕西', '西安市', '', 'zsb@xust.edu.cn', '雁塔校区：陕西省西安市碑林区雁塔中路58号,临潼校区：陕西省西安市临潼区秦唐大道48号', '710054', 'http://zs.xust.edu.cn', '029-85583041', '029-85583114', '西安科技大学历史悠久，底蕴深厚。学校办学历史可以追溯到1895年成立的北洋大学工学院采矿冶金科，1938年迁并于西北工学院矿冶系，1957年并入西安交通大学，1958年从西安交通大学分出成立独立的西安矿业学院，是当时隶属原煤炭工业部仅有的2所5年制本科院校之一。1998年学校实行“中央与地方共建，以地方管理为主”，划转陕西省。1999年更名为西安科技学院，2003年更名', '25', '7', '1', '133', 179, '170', '249', '0', '1198', '92.86', '15.66', '0.82', '1747', '3393', '66.00', '34.00', '2', '2');
