INSERT INTO `school` VALUES ('334', '陕西师范大学', '教育部', '297万', '3', '61', '1944', '2800', '西安师范学院,陕西师范学院', '普通本科', '师范类', '普通本科', '公办', '双一流', '{B: 10, B+: 6, B-: 2, C-: 2}', '陕西', '西安市', '长安区', 'jwzb@snnu.edu.cn', '雁塔校区：陕西省西安市长安南路199号,长安校区：陕西省西安市长安区西长安街620号', '710062', 'http://zsb.snnu.edu.cn', '029-85310330,029-85310188', '029-85308114', '陕西师范大学是教育部直属师范大学，国家首批“双一流”建设高校，是国家培养高等院校、中等学校师资和教育管理干部以及其他高级专门人才的重要基地，被誉为“教师的摇篮”。学校办学历史悠久，前身是1944年成立的陕西省立师范专科学校，1954年更名为西安师范学院，1960年与陕西师范学院合并，定名为陕西师范大学，1978年划归教育部直属。建校78年来，学校始终与民', '36', '18', '7', '3', 71, '75', '69', '79', '895', '92.13', '32.30', '1.96', '3579', '841', '19.03', '80.97', '2', '1');
