INSERT INTO `school` VALUES ('331', '安徽科技学院', '安徽省', '77万', '7', '34', '1950', '2000', '', '普通本科', '综合类', '普通本科', '公办', '', '{}', '安徽', '滁州市', '', '1035976518@qq.com', '蚌埠龙湖校区：安徽省蚌埠市龙子湖区大学城黄山大道1501号,凤阳校区：安徽省滁州市凤阳县东华路9号', '233100', 'http://www.ahstu.edu.cn/zsc/', '0552-3197041,0552-3197178,0550-6732041,0550-6732178', '', '安徽科技学院是一所办学历史悠久、文化底蕴深厚、多学科协调发展的省属本科院校。学校始建于1950年，1965年开始举办本科教育。历经安徽农学院凤阳分院、皖北农学院、安徽农业技术师范学院、安徽技术师范学院等变迁，2005年经教育部批准更名为安徽科技学院。70年栉风沐雨、披荆斩棘，一代代安科人薪火相传、砥砺奋进，学校事业长足发展。现有全日制本科生、硕士研究', '0', '0', '6', '1', 343, '340', '403', '0', '0', '94.26', '17.51', '0.60', '1832', '2814', '60.57', '39.43', '2', '2');
