INSERT INTO `school` VALUES ('324', '上海体育学院', '上海市', '25万', '0', '31', '1952', '1100', '', '普通本科', '体育类', '普通本科', '公办', '双一流', '{}', '上海', '上海市', '杨浦区', 'zb@sus.edu.cn', '上海市杨浦区长海路399号', '200438', 'http://bkzs.sus.edu.cn', '021-65506175,021-65506886', '021-65508900', '上海体育学院创建于1952年，是新中国成立最早的体育高等学府。2001年起，由国家体育总局和上海市人民政府共建共管。2017年，入选首批国家“双一流”和上海市高水平地方高校建设序列。学校坚持社会主义办学方向，扎根中国大地建设世界一流体育大学，坚持探索人才培养、学术创新、社会服务、文化传承、对外开放发展的体育特色创新发展路径。着力落实立德树人根本任', '6', '1', '1', '0', 0, '253', '274', '0', '0', '99.40', '10.40', '4.30', '443', '578', '56.60', '43.40', '2', '2');
INSERT INTO `school` VALUES ('323', '华东政法大学', '上海市', '65万', '0', '31', '1952', '1125', '华东政法学院', '普通本科', '政法类', '普通本科', '公办', '', '{A: 1, B: 1, C: 3}', '上海', '上海市', '松江区', 'zsb@ecupl.edu.cn', '上海市松江大学园区龙源路555号', '201620', 'http://zsb.ecupl.edu.cn/', '021-57090220', '', '华东政法大学是新中国创办的第一批高等政法院校。1952年6月，经华东军政委员会批准，圣约翰大学、复旦大学、南京大学、东吴大学、厦门大学、沪江大学、安徽大学、上海学院、震旦大学等9所院校的法律系、政治系和社会系合并，在圣约翰大学旧址成立华东政法学院。1958年，学校并入上海社会科学院；1963年再次筹建，次年招生；1966年停止招生，1972年被撤销；1979年3月，', '8', '2', '2', '0', 0, '138', '142', '0', '0', '96.03', '20.93', '11.37', '1921', '1000', '34.23', '65.77', '2', '2');
INSERT INTO `school` VALUES ('322', '安徽建筑大学', '安徽省', '130万', '1', '34', '1958', '1531', '', '普通本科', '理工类', '普通本科', '公办', '', '{C+: 9}', '安徽', '合肥市', '', '', '南校区：安徽省合肥市经济技术开发区紫云路292号,北校区：安徽省合肥市包河区金寨路856号', '230022', 'http://www.ahjzu.edu.cn/zsw/main.htm', '0551-63828083', '', '安徽建筑大学是安徽省唯一一所以土建类学科专业为特色的多科性大学，始建于1958年。学校是安徽省与住房和城乡建设部共建高校、教育部本科教学工作水平评估优秀院校、博士学位授予单位、国家“卓越工程师教育培养计划”实施高校、国家节约型公共机构示范单位、全国大学生社会实践先进单位、省人才工作先进单位、省优秀教学管理集体、省就业工作先进单位、省普通', '12', '1', '0', '521', 233, '230', '358', '0', '0', '93.73', '24.29', '1.19', '1182', '2801', '70.32', '29.68', '2', '2');
