INSERT INTO `school` VALUES ('295', '广州医科大学', '广东省', '101万', '1', '44', '1958', '558', '广州医学院', '普通本科', '医药类', '普通本科', '公办', '双一流', '{B-: 5, C: 3, C-: 2}', '广东', '广州市', '', 'zs8134@126.com', '番禺校区：广东省广州市番禺区新造镇,越秀校区：广东省广州市越秀区东风西路195号', '510182', 'http://zs.gzhmu.edu.cn', '020-83079671,020-37103483', '020-37103000', '广州医科大学创办于1958年，是一所以医学为优势和特色，开展博士、硕士、本科多层次人才培养的国家“双一流”建设高校。学校现有番禺、越秀两个校区，占地面积37.23万平方米，建筑面积48.65万平方米。下设22个学院，15所附属医院，28个研究机构。拥有1个国家医学中心，1个国家临床医学研究中心，1个国家重点实验室，11个省部级重点实验室，3个省部级工程技术研究中心/', '9', '5', '1', '2', 0, '144', '143', '0', '1251', '97.92', '0.00', '0.00', '688', '463', '40.22', '59.78', '2', '2');
