INSERT INTO `school` VALUES ('276', '川北医学院', '四川省', '130万', '0', '51', '1951', '1000', '南充医学专科学校', '普通本科', '医药类', '普通本科', '公办', '', '{}', '四川', '南充市', '嘉陵区', '2328605591@QQ.com', '高坪校区：四川省南充市高坪区东顺路55号,顺庆校区：四川省南充市顺庆区涪江路234号', '637000', 'https://admission.nsmc.edu.cn/', '0817-3352666', '', '川北医学院坐落在四川省南充市，现有顺庆和高坪两个校区，占地面积1000余亩。学校前身是1951年创办的西南区川北医士学校，1965年升格为专科，1985年升格为本科，定名为川北医学院。2005年开始招收留学生，2006年获得硕士学位授予权，2007年通过了教育部本科教学工作水平评估获得优秀，2014年成为四川省博士后创新实践基地，2016年获批四川省新增博士学位授予立项建设单位', '4', '0', '0', '0', 0, '366', '428', '0', '0', '95.29', '13.85', '0.22', '1748', '946', '35.12', '64.88', '2', '2');
INSERT INTO `school` VALUES ('275', '山西财经大学', '山西省', '135万', '0', '14', '1951', '1956', '', '普通本科', '财经类', '普通本科', '公办', '', '{B: 10, B+: 10, C: 1, C+: 2, C-: 5}', '山西', '太原市', '', 'zsb@sxufe.edu.cn', '坞城校区：山西省太原市坞城路140号,迎泽校区：山西省太原市南内环街135号,平阳校区：山西省太原市平阳路西二巷25号', '030006', 'http://zhaosheng.sxufe.edu.cn/?yikikata=77a31b6d-ce3a5067d200d7a1aec464d12c75bb2d', '0351-7666571,0351-7666573', '', '山西财经大学始建于1951年，是一所历史悠久、特色鲜明的高等院校，坐落于素有“龙城”之称的国家历史文化名城、山西省省会太原市。前身是山西省银行干部学校、商业干部学校、供销合作干部学校、财政干部学校和粮食干部学校，1958年9月五所干部学校合并成立山西财经学院。1984年12月成立山西经济管理学院。1997年10月山西财经学院和山西经济管理学院合并组建山西财经', '8', '4', '0', '0', 0, '149', '308', '0', '0', '90.51', '14.31', '4.43', '2833', '1340', '32.11', '67.89', '2', '2');
INSERT INTO `school` VALUES ('274', '西南医科大学', '四川省', '41万', '0', '51', '1951', '2011', '泸州医学院,四川医科大学', '普通本科', '医药类', '普通本科', '公办', '', '{C-: 4}', '四川', '泸州市', '', 'zs@swmu.edu.cn', '忠山校区：四川省泸州市忠山路3段319号,城北校区：四川省泸州市龙马潭区香林路1段1号', '646000', 'https://zsxxw.swmu.edu.cn/', '0830-3161658', '0830-3161222', '西南医科大学是四川省省属普通高等院校，坐落于川渝滇黔结合区域的国家历史文化名城、全国卫生城市、全国文明城市——泸州。学校前身西南区川南医士学校始建于1951年，1959年升格为泸州医学专科学校，1978年升格为本科院校并更名为泸州医学院，1982年获得学士学位授予权，1993年成为硕士学位授予单位，2001年开始联合培养博士，2004年起开展留学本科生学历教育，2010年获', '8', '0', '0', '0', 0, '319', '256', '0', '1702', '95.09', '16.22', '0.19', '2144', '1194', '35.79', '64.21', '2', '2');
