INSERT INTO `school` VALUES ('259', '中国美术学院', '浙江省', '0', '0', '33', '1928', '1000', '', '普通本科', '艺术类', '普通本科', '公办', '双一流', '{A+: 16, A-: 2, B+: 5}', '浙江', '杭州市', '上城区', 'zb@caa.edu.cn', '浙江省杭州市南山路218号', '310002', 'http://zb.caa.edu.cn', '0571-87164630', '', '西湖涌金，清波粼粼，象山叠翠，白鹭点点。中国美术学院的前身是国立艺术院。1928年，时任大学院长的蔡元培先生择址杭州西子湖畔，创立了第一所综合性的国立高等艺术学府——国立艺术院，揭开了中国高等美术教育的篇章。作为国内学科最完备、规模最齐整的第一所国立高等美术院校，中国美术学院在九十年的发展历史中，数迁其址，几易其名，从国立艺术院、国立杭', '12', '4', '2', '0', 0, '211', '459', '0', '0', '89.36', '0.00', '0.00', '1278', '489', '27.68', '72.32', '2', '2');
