INSERT INTO `school` VALUES ('239', '昌吉学院', '新疆维吾尔自治区', '30万', '0', '65', '1959', '1209', '', '普通本科', '综合类', '普通本科', '公办', '', '{}', '新疆', '昌吉回族自治州', '', 'xsczjkzjm@cjc.edu.cn', '北校区：新疆维吾尔自治区昌吉市北京北路77号,南校区：新疆维吾尔自治区昌吉市世纪大道南路9号', '831100', 'http://www.cjc.edu.cn/info/653', '0994-2337793', '0994-2345327', '昌吉学院位于新疆天山北坡经济带中心——美丽的昌吉市，有着60多年的办学历史。其前身是成立于1959年的昌吉师范学校，1985年升格为昌吉师范专科学校，2001年5月经国家教育部和自治区人民政府批准升格为普通本科院校——昌吉学院。学院现有两个校区，占地面积1209亩。其中，北京路校区194亩；世纪大道校区1015亩。目前，学院共有教职工1283人，其中专任教师964人，占教职', '0', '0', '0', '0', 583, '437', '0', '0', '0', '90.06', '4.36', '0.74', '826', '371', '30.99', '69.01', '2', '2');
