INSERT INTO `school` VALUES ('237', '新疆财经大学', '新疆维吾尔自治区', '72万', '0', '65', '1950', '946', '', '普通本科', '财经类', '普通本科', '公办', '', '{B-: 3, C: 1, C+: 2}', '新疆', '乌鲁木齐市', '', 'zsjy@xjufe.edu.cn', '新疆维吾尔自治区乌鲁木齐市北京中路449号', '830012', 'http://www.xjufe.edu.cn/zsjy/bkszs/index.htm', '0991-7804693', '', '新疆财经大学是新疆维吾尔自治区人民政府创办的重点大学。其前身是1950年成立的新疆省人民政府干部培训班；1959年升格为新疆财经学院；1962年转制为新疆财贸学校；1980年经国务院批准恢复新疆财经学院建制。2000年,新疆财经学院与新疆经济管理干部学院和新疆财政税务学校合并组建新的新疆财经学院；2007年经教育部批准，更名为新疆财经大学。学校校园总占地面积630502平', '6', '1', '0', '0', 0, '281', '570', '0', '0', '87.69', '3.98', '0.88', '1886', '852', '31.12', '68.88', '2', '2');
INSERT INTO `school` VALUES ('236', '喀什大学', '新疆维吾尔自治区', '60万', '0', '65', '1962', '2491', '喀什师范学院', '普通本科', '综合类', '普通本科', '公办', '', '{}', '新疆', '喀什地区', '', 'ksuzb@ksu.edu.cn', '高台校区：新疆维吾尔自治区喀什市学院路29号,新泉校区：新疆维吾尔自治区喀什市东城区学府大道', '844006', 'http://xgb.ksu.edu.cn/zsjy/bkszs.htm', '0998-2892165,0998-2892386,0998-2890007', '', '喀什大学坐落于中国历史文化名城、中国优秀旅游城市、素有“五口通八国、一路连欧亚”之称的喀什，是祖国最西部一所以培养基础教育师资和经济社会发展需要的应用型人才为使命的多科性本科学校。学校始建于1962年，前身为喀什师范专科学校，从建校到1978年底的16年间，是自治区5所高等院校（新疆大学、新疆工学院、八一农学院、新疆医学院、新疆喀什师范专科学校）', '5', '0', '0', '0', 529, '427', '0', '0', '0', '', '', '', '', '', '', '', '2', '2');
INSERT INTO `school` VALUES ('235', '新疆师范大学', '新疆维吾尔自治区', '100万', '1', '65', '1978', '2435', '', '普通本科', '师范类', '普通本科', '公办', '', '{B: 3, B-: 4, C+: 4}', '新疆', '乌鲁木齐市', '', 'zsb@xjnu.edu.cn', '温泉校区：新疆维吾尔自治区乌鲁木齐市水磨沟区观景路100号,昆仑校区：新疆维吾尔自治区乌鲁木齐市新医路102号', '830054', 'http://zhaosheng.xjnu.edu.cn', '0991-4332596,0991-4312920', '', '新疆师范大学是新疆维吾尔自治区重点建设的师范院校，1978年12月经教育部批准设立，其前身是乌鲁木齐第一师范学校和新疆教师培训部。新疆师范大学是党的十一届三中全会之后国家第一批新设立的高校之一，沐浴着党和国家改革开放的春风，四十多年来始终与改革开放同成长、共命运，走出了一条快速发展的路子。学校始终坚持社会主义办学方向，全面贯彻党的教育方针', '19', '6', '0', '0', 307, '216', '418', '0', '0', '89.58', '0.00', '0.00', '2028', '773', '27.60', '72.40', '2', '2');
INSERT INTO `school` VALUES ('234', '新疆医科大学', '新疆维吾尔自治区', '41万', '2', '65', '1954', '2165', '新疆医学院', '普通本科', '医药类', '普通本科', '公办', '', '{B-: 2, C: 2, C+: 5}', '新疆', '乌鲁木齐市', '', 'webmaster@xjmu.edu.cn', '雪莲山校区：新疆维吾尔自治区乌鲁木齐市水磨沟区尚德北路567号,新医路校区：新疆维吾尔自治区乌鲁木齐市新医路393号', '830011', 'https://xsc.xjmu.edu.cn/', '0991-2110666,0991-2110555', '', '新疆医科大学（XinjiangMedicalUniversity）位于乌鲁木齐市风景秀丽的雪莲山脚下，是新疆唯一一所省部委共建的医学高等学府。在毛泽东主席、周恩来总理的关心支持下，学校的前身“新疆医学院”被正式纳入国家“一五”计划156个重点项目之中。学校始建于1954年，1956年7月建成招生。1998年6月，新疆医学院与新疆中医学院合并成立新疆医科大学。2016年8月26日，学校正式获批成为', '13', '5', '8', '1', 0, '181', '265', '0', '0', '', '', '', '', '', '', '', '2', '2');
INSERT INTO `school` VALUES ('233', '新疆农业大学', '新疆维吾尔自治区', '140万', '2', '65', '1952', '6000', '', '普通本科', '农林类', '普通本科', '公办', '', '{C: 3, C+: 1, C-: 2}', '新疆', '乌鲁木齐市', '', 'zsjyc@xjau.edu.cn', '新疆维吾尔自治区乌鲁木齐市农大东路311号', '830052', '', '0991-8763007', '', '座落在新疆维吾尔自治区首府乌鲁木齐雅玛里克山脚下，巩宁古城遗址上的新疆农业大学，是新疆维吾尔自治区重点建设的一所以农业科学为优势和特色的大学。现任党委书记谢树青教授，校长蒋平安教授。学校前身为八一农学院，是经毛主席和中央军委同意，由王震将军于1952年在中国人民解放军第二步兵学校的基础上创建的，学校隶属于新疆军区，是新中国成立后新疆建立', '19', '8', '2', '0', 342, '176', '357', '0', '0', '', '', '', '', '', '', '', '2', '2');
INSERT INTO `school` VALUES ('232', '成都理工大学', '四川省', '237万', '2', '51', '1956', '2887', '成都理工学院', '普通本科', '理工类', '普通本科', '公办', '双一流', '{B+: 3, B-: 3, C: 2, C+: 2, C-: 6}', '四川', '成都市', '成华区', 'zs@cdut.edu.cn', '四川省成都市成华区二仙桥东三路1号', '610059', 'http://www.zs.cdut.edu.cn', '028-84078927', '', '成都理工大学是教育部与四川省共建的省属重点大学，是国家首批“双一流”建设高校。学校有成都和宜宾两个校区，占地面积3887亩，校舍建筑面积144.7万平方米，校园绿树成荫、繁花似锦，是读书治学的美好园地。成都理工大学的前身是创办于1956年的成都地质勘探学院，是共和国建国初期创办的三所地质院校之一。此后历经成都地质学院、成都理工学院的历史时期，2001年合', '22', '7', '3', '0', 173, '138', '184', '0', '1531', '92.22', '19.28', '4.39', '3498', '4125', '54.11', '45.89', '2', '2');
INSERT INTO `school` VALUES ('231', '西南民族大学', '国家民委', '300万', '0', '51', '1951', '3000', '', '普通本科', '民族类', '普通本科', '公办', '', '{B+: 1, C: 2, C+: 4, C-: 8}', '四川', '成都市', '', 'xnmdzb@swun.edu.cn', '武侯校区：四川省成都市一环路南四段16号,航空港校区：四川省成都市双流区航空港开发区大件路文星段168号', '610041', 'http://zs.swun.edu.cn', '028-85522012,028-85522089', '', '砥砺奋进，岁月流金。西南民族大学坐落于有“天府之国”美誉的国家历史文化名城——成都，染青城峨眉之灵秀，汲工部武侯之神韵，是民族高等教育镶嵌在祖国大西南的一颗明珠。学校是国家民族事务委员会直属综合性民族高校，前身为西南民族学院，创建于1950年7月，于1951年6月1日正式成立，2003年4月更名为西南民族大学。2005年，实现国家民委、成都市共建；2015年，实', '26', '4', '0', '0', 0, '162', '266', '0', '0', '87.20', '15.78', '1.85', '5146', '2726', '34.63', '65.37', '2', '2');
INSERT INTO `school` VALUES ('230', '中国医科大学', '辽宁省', '118万', '3', '21', '1931', '1949', '中国工农红军卫生学校', '普通本科', '医药类', '普通本科', '公办', '', '{B: 5, B+: 5, B-: 1}', '辽宁', '沈阳市', '沈北新区', 'bkzsb@cmu.edu.cn', '辽宁省沈阳市沈北新区蒲河路77号', '110122', 'http://www.cmu.edu.cn/bkzs/', '024-31939069', '', '中国医科大学是中国共产党最早创建的院校，是唯一以学校名义走完红军两万五千里长征全程并在长征中继续办学的院校，是我国最早进行西医学学院式教育的医学高校之一。其前身为1931年11月创建于江西瑞金的中国工农红军军医学校。1932年学校更名为中国工农红军卫生学校。1940年9月在延安，经毛泽东同志提议，中共中央批准，学校更名为中国医科大学。1948年11月，在沈阳', '0', '7', '5', '0', 0, '110', '98', '0', '1072', '91.49', '28.66', '2.62', '2587', '1383', '34.84', '65.16', '2', '2');
INSERT INTO `school` VALUES ('229', '东北财经大学', '辽宁省', '166万', '1', '21', '1952', '1125', '辽宁财经学院', '普通本科', '财经类', '普通本科', '公办', '', '{A: 8, A-: 6, B: 4, B+: 4, C: 1}', '辽宁', '大连市', '沙河口区', 'student@dufe.edu.cn', '辽宁省大连市沙河口区尖山街217号', '116025', 'https://zs.dufe.edu.cn/static/front/dufe/basic/html_cms/', '0411-84710258,0411-84710259', '', '东北财经大学以“培育卓越财经人才，服务经济社会发展”为使命，是一所突出经济学、管理学优势和特色，经济学、管理学、法学、文学、理学等多学科协调发展的研究型大学。学校始建于1952年，原名为东北财经学院，由东北财政专门学校、东北银行专门学校、东北计划统计学院及东北人民大学财政信贷系、会计统计系合并组建，校址在沈阳。其后，东北商业专科学校、东', '10', '6', '6', '0', 0, '92', '179', '0', '0', '94.41', '15.24', '22.03', '2315', '762', '24.76', '75.24', '2', '2');
INSERT INTO `school` VALUES ('228', '沈阳农业大学', '辽宁省', '112万', '3', '21', '1952', '10246', '', '普通本科', '农林类', '普通本科', '公办', '', '{B: 4, B-: 5, C: 1, C+: 2, C-: 3}', '辽宁', '沈阳市', '沈河区', 'syauzsb@126.com', '辽宁省沈阳市沈河区东陵路120号', '110866', 'http://zs.syau.edu.cn', '024-88490405', '', '沈阳农业大学坐落在国家历史文化名城沈阳市，东与世界文化遗产清福陵毗邻，南与沈水浑河相望，北倚天柱山，占地面积10246亩。校园环境幽雅，风景秀丽，人文气息浓厚，教学、科研条件优越。沈阳农业大学的办学历史可以追溯到1906年的省立奉天农业学堂。1952年全国高等院校调整，以复旦大学农学院与东北农学院部分系科为基础，成立了新的沈阳农学院。1979年经国务院批', '21', '10', '3', '10', 149, '138', '205', '0', '1626', '93.11', '30.00', '2.27', '', '', '', '', '2', '2');
INSERT INTO `school` VALUES ('227', '沈阳工业大学', '辽宁省', '283万', '1', '21', '1949', '2268', '', '普通本科', '理工类', '普通本科', '公办', '', '{B: 1, B+: 2, C+: 3}', '辽宁', '沈阳市', '', 'zhaoban_sut@163.com', '中央校区:辽宁省沈阳经济技术开发区沈辽西路111号,兴顺校区:辽宁省沈阳市铁西区兴顺街南十三路1号', '110870', 'http://zsxxw.sut.edu.cn/', '024-25496072', '', '沈阳工业大学是一所以工为主的多科性研究应用型大学，始建于1949年，1985年由沈阳机电学院更名为沈阳工业大学，原为国家机械工业部所属院校，1998年起由中央和地方共建，以辽宁省管理为主，在“双一流”建设中被列为全省冲击国内一流大学重点建设高校。学校由位于辽宁省沈阳市的中央校区、兴顺校区、国家大学科技园和位于辽阳市的辽阳分校组成，总占地面积151.19万', '20', '5', '2', '0', 210, '173', '298', '0', '0', '94.83', '19.15', '0.00', '1870', '3044', '61.95', '38.05', '2', '2');
INSERT INTO `school` VALUES ('226', '沈阳药科大学', '辽宁省', '0', '3', '21', '1931', '1995', '', '普通本科', '医药类', '普通本科', '公办', '', '{A: 6, B: 3}', '辽宁', '沈阳市', '沈河区', 'zs@syphu.edu.cn', '辽宁省沈阳市沈河区文化路103号', '110000', 'http://zs.syphu.edu.cn/', '024-43520666', '', '沈阳药科大学1931年始建于江西瑞金，前身是在中国共产党和毛主席领导关怀下创办的中国工农红军卫生学校调剂班。1934年，学校跟随红军长征，在两万五千里征途中历练，边走边学，边学边战，“马背上的药箱”精神与光荣传承至今，为祖国革命事业和药学事业发展培养了十多万名药学专门人才，是我国历史最悠久的综合性药科大学。学校总占地面积约133万平方米，总建筑面', '7', '2', '1', '1', 0, '209', '213', '0', '1579', '90.25', '29.00', '4.18', '1442', '641', '30.77', '69.23', '2', '2');
INSERT INTO `school` VALUES ('225', '辽宁师范大学', '辽宁省', '1万', '0', '21', '1951', '2100', '', '普通本科', '师范类', '普通本科', '公办', '', '{B-: 2, C+: 7, C-: 7}', '辽宁', '大连市', '沙河口区', '', '辽宁省大连市沙河口区850号', '116029', 'http://zsb.lnnu.edu.cn/', '0411-82158993', '', '辽宁师范大学是一所省属重点大学，坐落在中国北方美丽的海滨城市——大连，是辽宁省“双一流”建设“国内高水平大学”重点建设高校、教育部“卓越中学教师培养计划”实施学校、辽宁省教育事业发展联盟理事长单位，是辽宁省基础教育师资培养与继续教育基地、教育科学研究咨询基地、高校师资培训基地、适应地区经济建设与社会发展需要的科研基地，现已成为辽宁', '25', '9', '0', '0', 153, '185', '206', '0', '0', '96.14', '22.50', '0.00', '', '', '', '', '2', '2');
INSERT INTO `school` VALUES ('224', '沈阳大学', '辽宁省', '145万', '1', '21', '1905', '791', '', '普通本科', '综合类', '普通本科', '公办', '', '{C-: 4}', '辽宁', '沈阳市', '大东区', '', '辽宁省沈阳市大东区望花南街21号', '110044', 'https://zsjy.syu.edu.cn/zsxxw.htm', '024-62268563,024-62721597,024-62268564', '', '沈阳大学是经教育部批准并面向全国招生的全日制普通高等学校。学校办学历史可追溯至1905年建立的奉天实业学堂和1906年建立的新民公学堂。目前，学校已发展成为一所以本科教育为主体，同时拥有博士、硕士研究生教育和继续教育、留学生教育的综合性大学，是辽宁省办学历史最悠久、学科门类最齐全、人才培养层次最高的大学之一。近年来，学校先后成为教育部卓越工程', '10', '0', '0', '0', 303, '187', '526', '0', '0', '92.02', '8.81', '9.57', '2248', '1488', '39.83', '60.17', '2', '2');
INSERT INTO `school` VALUES ('223', '辽宁石油化工大学', '辽宁省', '153万', '1', '21', '1950', '1890', '', '普通本科', '理工类', '普通本科', '公办', '', '{B: 2, C+: 2, C-: 2}', '辽宁', '抚顺市', '望花区', 'Webmaster@lnpu.edu.cn', '辽宁省抚顺市望花区丹东路西段一号', '113001', 'http://zhaosheng.lnpu.edu.cn/', '024-56865000,024-56865007', '024-56865005', '辽宁石油化工大学是新中国第一所石油工业学校，1950年始建于大连，1953年迁至抚顺办学，1958年升格为抚顺石油学院，2000年2月由中国石油化工集团公司划转为辽宁省人民政府领导，2002年2月经教育部批准更名为辽宁石油化工大学，2010年3月辽宁省人民政府与中石油、中石化、中海油四方签署共建学校协议。建校71年来，学校已发展成为以石油石化为特色，工、理、经、管、文、', '14', '0', '0', '1', 260, '273', '369', '0', '0', '90.31', '14.28', '1.04', '1326', '2813', '67.96', '32.04', '2', '2');
INSERT INTO `school` VALUES ('222', '辽宁科技大学', '辽宁省', '149万', '1', '21', '1948', '2760', '', '普通本科', '理工类', '普通本科', '公办', '', '{B-: 3, C: 3, C+: 1, C-: 6}', '辽宁', '鞍山市', '', 'a5929096@126.com', '辽宁省鞍山市立山区千山中路189号', '114044', 'http://zsjy.ustl.edu.cn/index.htm', '0412-5929096', '', '辽宁科技大学是中国共产党领导的第一所冶金工业学校。1948年始建，1949年组建鞍山工业专门学校，1950年改为东北工学院鞍山分院，1958年升格为本科层次的鞍山钢铁学院，1998年由冶金工业部划转为辽宁省政府，实行中央和地方共建、以辽宁省管理为主的体制。经教育部批准，2002年更名为鞍山科技大学，2006年更名为辽宁科技大学。建校70余年来，学校已发展成为以工学为主，', '12', '3', '0', '0', 296, '255', '414', '0', '0', '95.75', '0.00', '0.02', '', '', '', '', '2', '2');
INSERT INTO `school` VALUES ('221', '沈阳师范大学', '辽宁省', '180万', '0', '21', '1951', '1845', '', '普通本科', '师范类', '普通本科', '公办', '', '{B: 3, C: 6, C+: 3, C-: 8}', '辽宁', '沈阳市', '皇姑区', 'synuzs@163.com', '辽宁省沈阳市皇姑区黄河北大街253号', '110034', 'http://zs.synu.edu.cn', '024-86574436,024-86592982', '024-86592995', '沈阳师范大学隶属辽宁省人民政府，始建于1951年，前身为东北教育学院，1953年更名为沈阳师范学院，是当时东北地区创办最早的两所本科师范院校之一。1965年更名为辽宁第一师范学院，1978年恢复沈阳师范学院校名。2000年，学校成功换建新校园，为学校事业发展奠定坚实基础。2002年，辽宁省政府决定并经教育部批准，沈阳师范学院与辽宁教育学院合并组建沈阳师范大学。2011', '25', '0', '0', '0', 220, '207', '293', '0', '0', '95.95', '15.12', '6.56', '3759', '1260', '25.10', '74.90', '2', '2');
INSERT INTO `school` VALUES ('220', '大连交通大学', '辽宁省', '140万', '5', '21', '1956', '1232', '大连铁道学院', '普通本科', '理工类', '普通本科', '公办', '', '{B: 1, C: 3, C+: 1, C-: 1}', '辽宁', '大连市', '沙河口区', 'xcbu@djtu.edu.cn', '辽宁省大连市沙河口区黄河路794号', '116028', 'http://recruit.djtu.edu.cn/', '0411-84106239,0411-84109197', '', '大连交通大学是东北地区唯一一所以轨道交通为特色的高等学校。1956年，学校应我国铁道机车车辆工业的发展需要而创建,时为大连机车车辆制造学校。1958年升格为大连铁道学院，隶属原铁道部管理。2000年2月，划转为辽宁省政府管理。2004年5月，更名为大连交通大学。在长期的办学历程中，学校始终坚守大学使命，坚持服务区域经济社会、轨道交通行业发展和国家战略需求，', '13', '2', '3', '0', 252, '230', '423', '0', '0', '90.11', '16.36', '1.82', '993', '2687', '73.02', '26.98', '2', '2');
INSERT INTO `school` VALUES ('219', '大连医科大学', '辽宁省', '98万', '1', '21', '1947', '2265', '大连医学院', '普通本科', '医药类', '普通本科', '公办', '', '{B: 6, B+: 1, C: 1, C+: 3}', '辽宁', '大连市', '', 'dy_zhaosheng@163.com', '辽宁省大连市旅顺南路西段9号', '116044', 'http://recruit.dmu.edu.cn/', '0411-86110222', '0411-86110023', '大连医科大学创建于1947年，前身为关东医学院。1949年并入大连大学，称大连大学医学院。1950年撤销大连大学建制，大连医学院独立。1969年大连医学院举校南迁贵州省遵义市，建立遵义医学院。1978年在大连市原址复办，仍称大连医学院。1994年更名为大连医科大学。学校现已发展成为以医学为主，理学、工学、管理学、艺术学、法学和哲学等多学科发展的医科大学，是辽宁省', '11', '4', '1', '0', 0, '160', '182', '0', '1470', '96.05', '27.13', '0.48', '2063', '1074', '34.24', '65.76', '2', '2');
INSERT INTO `school` VALUES ('218', '大连大学', '辽宁省', '160万', '60', '21', '1949', '1646', '大连工学院分院', '普通本科', '综合类', '普通本科', '公办', '', '{B-: 1, C: 1, C-: 1}', '辽宁', '大连市', '金州区', 'dldxzsb@126.com', '辽宁省大连市经济技术开发区学府大街10号', '116622', 'https://zsw.dlu.edu.cn/', '0411-87403633,0411-87403668', '', '大连大学以城市命名，是伴随着新中国成立而诞生的高等学校。70多年来，学校传承红色基因，赓续红色血脉，坚守为党育人、为国育才的初心使命，因大连市改革开放和经济社会发展对人才的迫切需求而立，在与城市互动中发展壮大，现已发展成为省内学科门类最齐全的高校。学校的历史可以追溯到始建于1949年4月成立的大连大学（1950年7月大连大学建制撤销，1978年10月成立', '19', '0', '7', '0', 253, '166', '355', '0', '0', '94.60', '10.99', '3.12', '2385', '1611', '40.32', '59.68', '2', '2');
INSERT INTO `school` VALUES ('217', '沈阳理工大学', '辽宁省', '166万', '1', '21', '1948', '1695', '', '普通本科', '理工类', '普通本科', '公办', '', '{C-: 3}', '辽宁', '沈阳市', '浑南区', '', '辽宁省沈阳市浑南区南屏中路6号', '110168', 'http://zs.sylu.edu.cn/', '024-24686088', '', '沈阳理工大学始建于1948年，是我军为培养新中国急需的兵工专门人才在东北地区创建的第一所本科军工高等学校，是共和国“兵工七子”之一，曾用名东北军工专门学校、沈阳工业学院等，先后隶属于中国人民解放军东北军工部、中央兵工总局、兵器工业部、国家机械工业委员会、机械电子工业部、中国兵器工业总公司等，1999年划归辽宁省管理，2004年更为现名。学校由辽宁', '12', '0', '5', '10', 284, '319', '454', '0', '0', '92.06', '14.59', '1.27', '1453', '2715', '65.14', '34.86', '2', '2');
INSERT INTO `school` VALUES ('216', '大连外国语大学', '辽宁省', '0', '2', '21', '1964', '1892', '大连外国语学院', '普通本科', '语言类', '普通本科', '公办', '', '{B: 15}', '辽宁', '大连市', '', '', '辽宁省大连市旅顺南路西段6号', '116044', 'http://zsb.dlufl.edu.cn', '0411-86111157,0411-86111158', '', '大连外国语大学地处辽宁省大连市，是辽宁省省属高校，是东北地区唯一一所公立外国语大学。学校于1964年在周恩来总理的亲切关怀下成立，时名大连日语专科学校，1970年更名为辽宁外语专科学校，1974年开办本科教育，1978年更名为大连外国语学院，2013年更名为大连外国语大学，目前已发展成为以外语为主，以国际化办学为特色，拥有文学、经济学、管理学、工学、法学、艺', '2', '0', '5', '0', 0, '253', '387', '0', '0', '95.14', '8.38', '24.09', '2810', '664', '19.12', '80.88', '2', '2');
INSERT INTO `school` VALUES ('215', '沈阳化工大学', '辽宁省', '88万', '0', '21', '1952', '1425', '沈阳化工学院,辽宁科学技术大学', '普通本科', '理工类', '普通本科', '公办', '', '{B: 3, C-: 2}', '辽宁', '沈阳市', '', '', '辽宁省沈阳市经济技术开发区11号街', '110000', 'http://zhaosheng.syuct.edu.cn', '024-89388461,024-89388469', '', '沈阳化工大学是一所以工为主、以化工为特色，工、理、管、经、文、法、医等7大学科门类相结合的多科性综合大学，是国家最早在东北设置的化工类高校，是辽宁省“双一流”重点建设高校、国家“中西部高校基础能力建设工程”重点建设高校（“小211”）、教育部卓越工程师教育培养计划高校、教育部高等学校特色专业建设单位、全国深化创新创业教育改革示范高校、国', '14', '1', '0', '1', 263, '243', '450', '0', '0', '92.71', '14.53', '6.67', '1341', '2087', '60.88', '39.12', '2', '2');
INSERT INTO `school` VALUES ('214', '渤海大学', '辽宁省', '140万', '0', '21', '1950', '2250', '', '普通本科', '综合类', '普通本科', '公办', '', '{C: 6, C-: 4}', '辽宁', '锦州市', '', 'bhdxzsb@yahoo.com.cn', '松山校区：辽宁省锦州高新技术产业开发区科技路19号,滨海校区：辽宁省锦州经济技术开发区（滨海新区）金山大街20号', '121000', 'http://zsxx.bhu.edu.cn/page/depart/zsxx/benke/', '0416-3400131,0416-3400132', '0416-3400015', '渤海大学始建于1950年2月，是辽宁省政府主办的综合性大学，位于渤海之滨的历史文化名城辽宁省锦州市。学校为具有推荐优秀应届本科毕业生免试攻读硕士研究生资格院校和中国政府奖学金来华留学生培养院校。学校占地2250余亩，总建筑面积60万平方米。设有19个二级学院，49个本科招生专业，拥有18个一级学科硕士学位授权点和10个专业硕士学位授权点，涵盖经、法、教、文', '18', '0', '0', '0', 268, '273', '263', '0', '988', '93.51', '17.85', '1.62', '3388', '1251', '26.96', '73.04', '2', '2');
INSERT INTO `school` VALUES ('213', '辽宁工业大学', '辽宁省', '78万', '0', '21', '1951', '945', '', '普通本科', '理工类', '普通本科', '公办', '', '{C+: 2, C-: 2}', '辽宁', '锦州市', '古塔区', 'lngydxzs@lnut.edu.cn', '辽宁省锦州市古塔区士英街169号', '121001', 'https://zjc.lnut.edu.cn/index/zsw/index.htm', '0416-4198703', '', '辽宁工业大学是一所以工为主，理、工、经、管、文、法、艺术等协调发展的多科性应用型大学，始建于1951年，历经“锦州工学院”“辽宁工学院”等发展阶段，2007年3月更名为“辽宁工业大学”。学校2013年入选国家“中西部高校基础能力建设工程”重点建设高校，2016年获批辽宁省首批深化创新创业教育改革示范高校，2017年获批辽宁省普通本科高等学校向应用型转变示范高', '10', '0', '0', '0', 290, '319', '344', '0', '0', '95.88', '11.37', '0.35', '1380', '2331', '62.81', '37.19', '2', '2');
INSERT INTO `school` VALUES ('212', '沈阳建筑大学', '辽宁省', '122万', '1', '21', '1948', '1500', '', '普通本科', '理工类', '普通本科', '公办', '', '{B: 3, B+: 5, B-: 1, C+: 1}', '辽宁', '沈阳市', '浑南区', 'zsb@sjzu.edu.cn', '辽宁省沈阳市浑南区浑南东路25号', '110168', 'http://111.117.146.136/Portal/Default.aspx', '024-24693969', '', '沈阳建筑大学地处国家区域性中心城市——沈阳，位于风光壮美的浑河南岸。学校是一所以建筑、土木等学科为特色和优势，以工为主，工、管、理、文、农、艺术等多学科门类协调发展的省部共建高等学校。原隶属于国家建设部，2000年在国家办学管理体制调整中划归辽宁省管理，2010年成为国家住房和城乡建设部与辽宁省人民政府共建高校。学校诞生在解放战争时期，是一所', '16', '5', '0', '1', 224, '202', '342', '0', '0', '95.74', '16.14', '3.17', '1111', '1783', '61.66', '38.34', '2', '2');
INSERT INTO `school` VALUES ('211', '大连工业大学', '辽宁省', '76万', '2', '21', '1958', '758', '', '普通本科', '理工类', '普通本科', '公办', '', '{B+: 1, C: 4, C-: 2}', '辽宁', '大连市', '甘井子区', 'zsbgs@dlpu.edu.cn', '辽宁省大连市甘井子区轻工苑1号', '116034', 'http://zsb.dep.dlpu.edu.cn/', '0411-86323661,0411-86323693', '', '大连工业大学创建于1958年，是我国最早建立的4所轻工业院校之一，原隶属轻工业部，1998年转制为中央与地方共建、辽宁省管理为主的体制。2007年3月经教育部批准，学校由大连轻工业学院更名为大连工业大学。半个多世纪来，学校逐渐发展成为以工为主，工、理、艺、文、管、经六大学科门类协调发展，以培养食品、轻工、纺织、艺术等专业人才为办学特色的高等学府。学校', '14', '3', '0', '0', 254, '185', '367', '0', '0', '92.72', '16.05', '3.39', '2196', '1524', '40.97', '59.03', '2', '2');
INSERT INTO `school` VALUES ('210', '大连海洋大学', '辽宁省', '112万', '2', '21', '1952', '1110', '大连水产学院', '普通本科', '农林类', '普通本科', '公办', '', '{B: 4, C: 3}', '辽宁', '大连市', '', 'zsb@dlou.edu.cn', '黄海校区：辽宁省大连市沙河口区黑石礁街52号,渤海校区：辽宁省大连市甘井子区营平路288号', '116023', 'http://bkzsw.dlou.edu.cn/', '0411-84763156,0411-84763157,0411-84763158', '0411-84762652', '大连海洋大学是我国北方地区唯一的一所以海洋和水产学科为特色，农、工、理、管、文、法、经、艺等学科协调发展的多科性高等院校。学校创建于1952年，前身为东北水产技术学校，1958年升格为大连水产专科学校，1978年升格为大连水产学院。2000年由农业部划转辽宁省管理。2010年经教育部批准更名为大连海洋大学。学校坐落于美丽的海滨城市大连。有黄海校区、渤海校区和', '13', '0', '0', '0', 295, '256', '491', '0', '0', '94.42', '14.56', '0.99', '1414', '1917', '57.55', '42.45', '2', '2');
INSERT INTO `school` VALUES ('209', '沈阳航空航天大学', '辽宁省', '116万', '0', '21', '1952', '1732', '沈阳航空工业学院', '普通本科', '理工类', '普通本科', '公办', '', '{B-: 3, C: 10, C-: 4}', '辽宁', '沈阳市', '沈北新区', 'zsb@sau.edu.cn', '辽宁省沈阳市沈北新区道义南大街37号', '110136', 'http://zs.sau.edu.cn/', '024-89724466', '', '沈阳航空航天大学始建于1952年，学校现已建设成为以航空宇航为特色，以工为主，工、理、文、经、管、艺等学科协调发展的多科性高等院校，是辽宁省人民政府与教育部、中国航空工业集团公司三方共建高校，是辽宁省人民政府与国家国防科工局共建高校。学校先后获评全国先进基层党组织、全国模范职工之家、全国优秀易班共建高校等荣誉称号。学校占地面积1731.63亩，建', '15', '1', '0', '0', 223, '253', '331', '0', '0', '95.71', '0.00', '0.00', '1000', '2400', '70.59', '29.41', '2', '2');
INSERT INTO `school` VALUES ('208', '大连民族大学', '国家民委', '0', '7', '21', '1984', '1600', '大连民族学院', '普通本科', '民族类', '普通本科', '公办', '', '{}', '辽宁', '大连市', '', 'zs@dlnu.edu.cn', '辽宁省大连经济技术开发区辽河西路18号', '116600', 'http://zs.dlnu.edu.cn', '0411-87656067', '', '大连民族大学位于美丽的海滨城市大连，隶属于国家民族事务委员会，是全国110多所中央部属高校之一，是国家唯一设在东北和沿海开放地区、以工科为主多学科协调发展的民族高等学校，学校建设目标为高水平现代化综合大学。学校现有开发区和金石滩两个校区，占地面积1600多亩。学校1984年立项筹建，1993年招生办学，是国家民委、教育部、辽宁省政府、大连市政府共建高', '2', '0', '12', '1', 0, '256', '426', '0', '0', '94.13', '12.66', '9.89', '2402', '1703', '41.49', '58.51', '2', '2');
INSERT INTO `school` VALUES ('207', '锦州医科大学', '辽宁省', '97万', '0', '21', '1946', '1493', '辽宁医学院', '普通本科', '医药类', '普通本科', '公办', '', '{C: 3, C-: 1}', '辽宁', '锦州市', '', 'zs@lnmu.edu.cn', '辽宁省锦州市松坡路三段40号', '121000', 'http://jzmuzs.university-hr.com/', '0416-4673536,0416-4673436', '', '锦州医科大学诞生于解放战争时期，学校前身为1946年在吉林省洮南市成立的辽吉军区卫生学校。1949年迁址辽宁省锦州市，1958年成立锦州医学院，2006年更名为辽宁医学院。2007年以优秀的成绩通过国家教育部本科教学工作水平评估。2016年3月，经国家教育部批准，正式更名为锦州医科大学。经过75年的发展已成为一所以医学为主，多门类、多层次、多种办学形式的省属普通高等', '8', '0', '0', '0', 0, '293', '366', '0', '0', '94.03', '20.97', '0.26', '1381', '579', '29.54', '70.46', '2', '2');
INSERT INTO `school` VALUES ('206', '沈阳音乐学院', '辽宁省', '55万', '0', '21', '1938', '690', '', '普通本科', '艺术类', '普通本科', '公办', '', '{B+: 1}', '辽宁', '沈阳市', '和平区', 'sy10177@163.com', '辽宁省沈阳市和平区三好街61号', '110818', 'http://www.sycm.edu.cn/info.aspx?DWid=66', '024-23894405', '', '沈阳音乐学院的前身是1938年由毛泽东、周恩来等老一辈无产阶级革命家在延安倡导成立的鲁迅艺术学院，是中国共产党创办的第一所高等艺术学院，毛泽东亲笔题写了“紧张、严肃、刻苦、虚心”的校训。抗日战争胜利后，学院由延安迁至东北，1949年更名为东北鲁迅文艺学院。1953年在东北鲁迅文艺学院音乐部的基础上，成立了东北音乐专科学校，1958年更名为沈阳音乐学院。', '2', '0', '0', '0', 0, '319', '439', '0', '0', '93.82', '4.07', '1.77', '1420', '700', '33.02', '66.98', '2', '2');
