INSERT INTO `school` VALUES ('175', '江西财经大学', '江西省', '643万', '0', '36', '1923', '2200', '', '普通本科', '财经类', '普通本科', '公办', '', '{A-: 8, B: 5, B+: 7, B-: 3, C-: 1}', '江西', '南昌市', '', 'wts1@jxufe.edu.cn', '蛟桥园校区：江西省南昌市国家经济技术开发区双港东大街169号,麦庐园校区：江西省南昌市国家经济技术开发区玉屏大道', '330013', 'http://zsjy.jxufe.edu.cn/web/index.html', '0791-88678121,0791-88678170,0791-83822820', '', '江西财经大学是一所财政部、教育部、江西省人民政府共建，以经济、管理类学科为主，法、工、文、理、艺术等学科协调发展的高等财经学府。2008年2月6日，温家宝总理与江西财大师生共度除夕，发表了重要讲话，并称赞说：“你们学校是所很好的学校”，激励着学校永葆奋斗精神，办好人民满意的教育。1.办学历史：江西财经大学前身为1923年秋创办的江西省立商业学校,1958', '13', '7', '1', '0', 0, '101', '155', '0', '0', '92.17', '13.46', '9.67', '', '', '', '', '2', '2');
