INSERT INTO `school` VALUES ('129', '延边大学', '吉林省', '200万', '1', '22', '1949', '2940', '', '普通本科', '综合类', '普通本科', '公办', '双一流', '{B+: 4, B-: 2, C: 5, C-: 8}', '吉林', '延边朝鲜族自治州', '延吉市', 'ydzb@ybu.edu.cn', '延吉校区：吉林省延边朝鲜族自治州延吉市公园街道园校社区公园路977号,珲春校区：吉林省延边朝鲜族自治州珲春市靖和街道龙泉社区站前大街5588号,师范分院校区：吉林省延边朝鲜族自治州延吉市新兴街道民和社区参花街2855号,科学技术学院校区：吉林省延边朝鲜族自治州延吉市北山街道丹春社区朝阳街3458号', '133002', 'http://zsb.ybu.edu.cn/', '0433-2732477', '', '延边大学(YanbianUniversity)坐落在享有“教育之乡”美誉的吉林省延边朝鲜族自治州首府延吉市，是国家“双一流”建设高校、国家“211工程”重点建设大学、西部开发重点建设院校、吉林省人民政府和教育部共同重点支持建设大学、吉林省人民政府和国家民委共同支持建设大学。学校始建于1949年，是中国共产党较早在少数民族地区建立的高校之一。现设有22个学院，76个本科专', '32', '10', '2', '1', 185, '170', '291', '0', '1654', '70.80', '33.53', '4.42', '3896', '2149', '35.55', '64.45', '2', '1');
INSERT INTO `school` VALUES ('128', '武汉理工大学', '教育部', '350万', '5', '42', '1898', '4000', '', '普通本科', '理工类', '普通本科', '公办', '双一流', '{A+: 1, B: 13, B+: 7, B-: 12, C: 1, C+: 4, C-: 3}', '湖北', '武汉市', '洪山区', 'zhaosheng@whut.edu.cn', '湖北省武汉市洪山区珞狮路122号;湖北省武汉市武昌区和平大道1040号', '430070', 'http://zs.whut.edu.cn', '027-87859017,027-87858399', '', '武汉理工大学是教育部直属全国重点大学，是首批列入国家“211工程”和“双一流”建设高校，是教育部和交通运输部等部委共建高校。学校办学历史起源于1898年建立的湖北工艺学堂，办学123年特别是近70年来，学校共培养了60余万名高级专门人才，是教育部直属高校中为建材建工、交通、汽车三大行业培养人才规模最大的学校，已成为我国“三大行业”高层次人才培养和科技', '45', '22', '5', '35', 55, '35', '36', '58', '371', '95.29', '37.00', '6.00', '2570', '5964', '69.89', '30.11', '2', '1');
INSERT INTO `school` VALUES ('127', '华中科技大学', '教育部', '579万', '16', '42', '1952', '7000', '', '普通本科', '综合类', '普通本科', '公办', '双一流', '{A: 5, A+: 2, A-: 15, B: 8, B+: 26, B-: 5, C: 1, C+: 2}', '湖北', '武汉市', '洪山区', 'zs@hust.edu.cn', '湖北省武汉市洪山区珞喻路1037号;湖北省武汉市汉口航空路13号', '430074', 'http://zsb.hust.edu.cn/', '027-87542228,027-87559946', '027-87541114', '华中科技大学是国家教育部直属重点综合性大学，由原华中理工大学、同济医科大学、武汉城市建设学院于2000年5月26日合并成立，是国家“211工程”重点建设和“985工程”建设高校之一，是首批“双一流”建设高校。学校校园占地7000余亩，园内树木葱茏，碧草如茵，环境优雅，景色秀丽，绿化覆盖率72%，被誉为“森林式大学”。学校教学科研支撑体系完备，各项公共服务设施', '48', '46', '31', '3279', 8, '9', '6', '17', '176', '95.70', '43.39', '14.50', '2248', '4864', '65.39', '34.61', '1', '1');
INSERT INTO `school` VALUES ('126', '山东大学', '教育部', '792万', '21', '37', '1901', '8000', '', '普通本科', '综合类', '普通本科', '公办', '双一流', '{A: 4, A+: 2, A-: 17, B: 8, B+: 23, B-: 5, C: 1, C+: 3}', '山东', '济南市', '历城区', 'sdbkzs@sdu.edu.cn', '山东省济南市历城区山大南路27号', '250100', 'http://www.bkzs.sdu.edu.cn/', '0531-59626977', '0531-88395114', '山东大学是一所历史悠久、学科齐全、实力雄厚、特色鲜明的教育部直属重点综合性大学，在国内外具有重要影响，2017年顺利迈入世界一流大学建设高校（A类）行列。山东大学前身是1901年创办的山东大学堂，被誉为中国近代高等教育起源性大学。其医学学科起源于1864年，开启近代中国高等医学教育之先河。从诞生起，学校先后历经了山东大学堂、国立青岛大学、国立山东大', '51', '44', '28', '233', 22, '14', '15', '22', '399', '90.94', '38.42', '10.23', '3230', '3467', '51.70', '48.30', '1', '1');
