INSERT INTO `school` VALUES ('95', '天津美术学院', '天津市', '20万', '0', '12', '1906', '226', '', '普通本科', '艺术类', '普通本科', '公办', '', '{B+: 3, B-: 1}', '天津', '天津市', '河北区', 'tjmyzhb@163.com', '天津市河北区天纬路4号', '300141', 'http://zsbgs.tjarts.edu.cn/index/bkzs.htm', '022-26241719,022-26242184', '', '天津美术学院前身为北洋女师范学堂，1906年由近代著名教育家傅增湘先生创办，是我国最早的公立高等学府之一，1959年发展为河北美术学院，成为独立建制的高等美术院校，1980年正式定名为天津美术学院，是一所学科专业齐全、实力雄厚的专业型高等美术院校，为我国独立设置的八所美术学院之一，为国家级人才培养模式创新实验区。学校现有天纬路、志成路两个校区，校', '3', '0', '0', '0', 0, '398', '652', '0', '0', '95.39', '7.55', '2.45', '575', '445', '43.63', '56.37', '2', '2');
INSERT INTO `school` VALUES ('94', '天津音乐学院', '天津市', '7万', '0', '12', '1958', '140', '', '普通本科', '艺术类', '普通本科', '公办', '', '{B+: 2}', '天津', '天津市', '河东区', 'zsb@tjcm.edu.cn', '天津市河东区十四经路9号', '300171', 'http://www.tjcm.edu.cn/zsxx/bkzs.htm', '022-24160044', '', '天津音乐学院建于1958年，是全国首批有硕士学位和学士学位授予权的院校之一。天津音乐学院首任院长为著名音乐教肓家、理论家缪天瑞。经过六十多年的建设，学院已经发展成为培养音乐、舞蹈、戏剧、影视专门人才的重要高等艺术教育学府。现拥有艺术学理论、音乐与舞蹈学、戏剧与影视学3个一级学科，1个专业硕士（MFA）授权点，音乐与舞蹈学、戏剧与影视学为市级重', '3', '0', '0', '0', 0, '398', '682', '0', '0', '94.55', '6.93', '0.00', '', '', '', '', '2', '2');
INSERT INTO `school` VALUES ('93', '天津农学院', '天津市', '67万', '0', '12', '1976', '1292', '', '普通本科', '农林类', '普通本科', '公办', '', '{C: 4}', '天津', '天津市', '西青区', 'tjauzb@126.com', '东校区：天津市西青区津静路22号,西校区：天津市西青区津同路19号,蓟州校区：天津市蓟州区五百户镇青池1村', '300384', 'http://zjb.tjau.edu.cn/zsw.htm', '022-23799551', '', '天津农学院始建于1976年。2001年经天津市政府批准，天津市城乡经济学校并入天津农学院。1982年获学士学位授予权，2006年获硕士学位授予权。1996年首批通过国家教委本科教学合格评价，2007年在教育部进行的本科教学工作水平评估中获得“优秀”。学校以农科为主体，农学、工学、管理学、理学、经济学、文学、艺术学协调发展。经过多年的办学实践，形成了紧贴区域经济社', '9', '3', '0', '0', 352, '316', '560', '0', '0', '92.94', '9.92', '0.00', '1669', '1163', '41.07', '58.93', '2', '2');
