INSERT INTO `school` VALUES ('85', '天津商业大学', '天津市', '173万', '0', '12', '1980', '1340', '', '普通本科', '财经类', '普通本科', '公办', '', '{C: 5, C-: 6}', '天津', '天津市', '北辰区', 'tszsb@tjcu.edu.cn', '天津市北辰区光荣道409号', '300134', 'http://zs.tjcu.edu.cn', '022-26656785', '022-26685774', '天津商业大学原名天津商学院，由国家商业部和天津市人民政府于1980年创建。1998年学校实行中央与地方共建、以地方管理为主的管理体制。2007年学校更名为天津商业大学。学校位于天津市北辰区光荣道409号，占地面积近1340亩，建筑面积近51万平方米，教学科研仪器设备总值5.56亿元。图书馆现有中外文藏书220.87万册，每年订阅中外文报刊756种（册），中外文数据库56个，电子', '11', '0', '0', '0', 0, '250', '364', '0', '0', '76.34', '9.95', '5.55', '3632', '1647', '31.20', '68.80', '2', '2');
