INSERT INTO `school` VALUES ('78', '天津医科大学', '天津市', '60万', '0', '12', '1951', '600', '', '普通本科', '医药类', '普通本科', '公办', '双一流', '{B: 8, B+: 1, B-: 1, C+: 1}', '天津', '天津市', '和平区', 'zhaoban@tijmu.edu.cn', '天津市和平区气象台路22号', '300070', 'http://www.tmu.edu.cn/bkzs/', '022-83336577', '', '天津医科大学的前身天津医学院创建于1951年，是新中国成立后原国家政务院批准建立的高等医学院校。著名内分泌学家、医学教育家朱宪彝教授为首任校长。1994年6月天津医学院与天津第二医学院正式组建成立天津医科大学。1996年12月成为天津市唯一的国家“211工程”重点建设市属院校，2015年10月成为天津市人民政府、国家卫生健康委员会和教育部共建高校，2017年9月入选国家', '12', '10', '5', '64', 0, '115', '124', '0', '1127', '78.64', '54.30', '1.53', '735', '440', '37.45', '62.56', '2', '1');
