INSERT INTO `school` VALUES ('72', '安徽医科大学', '安徽省', '125万', '4', '34', '1926', '2086', '东南医学院,安徽医学院', '普通本科', '医药类', '普通本科', '公办', '', '{B: 5, B-: 7, C: 1, C+: 3, C-: 6}', '安徽', '合肥市', '合肥经济技术开发区', 'ahmuzsb@ahmu.edu.cn', '梅山路校区：安徽省合肥市梅山路81号,翡翠路校区：安徽省合肥市经济开发区翡翠路15号', '230032', 'http://zs.ahmu.edu.cn/', '0551-65167746', '', '安徽医科大学是安徽省办学历史最早的高等学校之一，是安徽省第一所高等医科院校。学校坐落在有“江南唇齿，淮右襟喉”之称的历史文化名城——合肥，承立德树人之使命，育精医报国之英才。学校是安徽省属重点大学，是教育部、国家卫健委和安徽省共建高校，是安徽省第一批地方特色高水平大学和第一批综合改革试点学校。学校前身是1926年始创于上海的东南医科大学', '14', '4', '1', '0', 0, '175', '140', '0', '1460', '94.37', '0.00', '0.00', '1425', '962', '40.30', '59.70', '2', '2');
