INSERT INTO `school` VALUES ('56', '河北经贸大学', '河北省', '0', '0', '13', '1995', '3000', '', '普通本科', '财经类', '普通本科', '公办', '', '{C: 3, C+: 16}', '河北', '石家庄市', '', 'zhaosheng@heuet.edu.cn', '河北省石家庄市学府路47号', '050061', 'http://zhaosheng.heuet.edu.cn', '0311-87655611', '', '河北经贸大学是河北省重点建设的骨干大学，是一所以经济学、管理学、法学为主，兼有文学、理学、工学和艺术学的多学科财经类大学。学校由河北财经学院、河北经贸学院、河北商业高等专科学校等合并组建而成，至今已有69年的办学历史。学校分北、南、西三个校区办学。其中南、西校区为独立学院——经济管理学院，座落于石家庄市西南高教区，占地200余亩。北校区是', '10', '0', '0', '0', 0, '226', '388', '0', '0', '84.47', '13.97', '1.44', '3735', '1527', '29.02', '70.98', '2', '2');
