INSERT INTO `school` VALUES ('52', '北京师范大学', '教育部', '290万', '17', '11', '1902', '1191', '', '普通本科', '师范类', '普通本科', '公办', '双一流', '{A: 1, A+: 16, A-: 11, B: 4, B+: 7, B-: 1, C+: 6, C-: 3}', '北京', '北京市', '海淀区', 'zsb@bnu.edu.cn', '北京市新街口外大街19号', '100875', 'https://admission.bnu.edu.cn/', '010-58807962', '010-58806183', '北京师范大学是教育部直属重点大学，是一所以教师教育、教育科学和文理基础学科为主要特色的著名学府。学校的前身是1902年创立的京师大学堂师范馆，1908年改称京师优级师范学堂，独立设校，1912年改名为北京高等师范学校。1923年学校更名为北京师范大学，成为中国历史上第一所师范大学。1931年、1952年北平女子师范大学、辅仁大学先后并入北京师范大学。百余年来，北', '38', '32', '26', '39', 17, '10', '24', '13', '295', '99.31', '39.59', '15.72', '', '', '', '', '1', '1');
INSERT INTO `school` VALUES ('51', '西南交通大学', '教育部', '700万', '4', '51', '1896', '5000', '', '普通本科', '理工类', '普通本科', '公办', '双一流', '{A+: 2, A-: 2, B: 4, B+: 8, B-: 5, C: 4, C+: 13}', '四川', '成都市', '郫都区', 'zsks@swjtu.edu.cn', '犀浦校区：四川省成都市郫都区犀安路999号,九里校区：四川省成都市二环路北一段111号,峨眉校区：四川省峨眉山市景区路二段6号', '610031', 'https://zhaosheng.swjtu.edu.cn/', '028-66366373,028-66368074', '', '西南交通大学是教育部直属全国重点大学，国家首批“双一流”“211工程”“特色985工程”“2011协同创新计划”重点建设并设有研究生院的研究型大学，坐落于中国历史文化名城、国家中心城市——成都。学校创建于1896年，前身为山海关北洋铁路官学堂（ImperialChineseRailwayCollege)，是中国第一所工程教育高等学府，是中国土木工程、矿冶工程、交通工程高等教育的发祥地，同时', '40', '18', '7', '746', 52, '50', '48', '89', '701', '96.55', '28.30', '5.01', '2131', '4333', '67.03', '32.97', '2', '1');
