INSERT INTO `school` VALUES ('49', '北京中医药大学', '教育部', '88万', '1', '11', '1956', '1625', '', '普通本科', '医药类', '普通本科', '公办', '双一流', '{A+: 3, B+: 2}', '北京', '北京市', '朝阳区', 'zs@bucm.edu.cn', '北京市朝阳区北三环东路11号', '100029', 'http://bkzs.bucm.edu.cn', '010-64286231', '', '北京中医药大学是一所以中医药学为主干学科的全国重点大学，直属教育部管理，由教育部、国家卫生健康委员会、国家中医药管理局和北京市共建。学校现任党委书记谷晓红，校长徐安龙。北京中医药大学始建于1956年，前身为北京中医学院，是国务院批准最早创办的高等中医药院校。1960年，被中央确定为全国重点高校；1993年，更名为北京中医药大学；1996年，入选国家“211', '7', '3', '3', '2', 0, '99', '137', '62', '1501', '94.73', '39.94', '1.12', '2360', '867', '26.87', '73.13', '2', '1');
INSERT INTO `school` VALUES ('48', '北京邮电大学', '教育部', '171万', '4', '11', '1955', '693', '北京邮电学院', '普通本科', '理工类', '普通本科', '公办', '双一流', '{A: 1, A+: 2, A-: 1, B+: 1, B-: 2, C+: 2}', '北京', '北京市', '海淀区', 'zsb@bupt.edu.cn', '北京市西土城路10号', '100876', 'https://zsb.bupt.edu.cn/#1307', '010-62282045,010-62285045,010-62280945,010-62289945', '', '六十余载风雨砥砺，六十余载春华秋实。明光之北、蓟门之南，古老的城墙，见证了永不消逝的电波；鸿雁翱翔、银杏巍巍，坚实的土地，承载了信息黄埔的传奇。北京邮电大学是教育部直属、工业和信息化部共建、首批进行“211工程”建设的全国重点大学，是“985优势学科创新平台”项目重点建设高校，是一所以信息科技为特色、工学门类为主体、工管文理协调发展的多科', '24', '11', '2', '13', 62, '57', '85', '36', '760', '99.14', '44.39', '21.30', '1372', '2003', '59.35', '40.65', '2', '1');
