INSERT INTO `school` VALUES ('45', '中央民族大学', '国家民委', '140万', '0', '11', '1941', '570', '', '普通本科', '民族类', '普通本科', '公办', '双一流', '{A+: 1, B: 3, B+: 5, B-: 4, C: 3, C+: 5, C-: 5}', '北京', '北京市', '海淀区', 'zb@cun.edu.cn', '北京市海淀区中关村南大街27号', '100081', 'http://zb.muc.edu.cn', '010-68932902,010-68933922', '010-68932442', '中央民族大学（MinzuUniversityofChina）坐落于北京学府林立的海淀区，南邻国家图书馆，北依中关村科技园，校园环境典雅，古朴幽美，人文氛围浓郁，具有鲜明的民族特色。坐落于青龙湖畔的丰台校区于2021年9月正式启用，具有中式经典、山水校园风貌特点，彻底提升了“大文化”与“小空间”的匹配度，为中央民族大学跨越式发展提供了新起点。中央民族大学是党和国家为解', '27', '6', '5', '3', 0, '85', '190', '90', '0', '92.01', '32.70', '0.00', '1966', '833', '29.76', '70.24', '1', '1');
