INSERT INTO `school` VALUES ('41', '河北工业大学', '河北省', '102万', '0', '12', '1903', '4000', '', '普通本科', '理工类', '普通本科', '公办', '双一流', '{B: 3, B+: 2, B-: 3, C: 3, C+: 7, C-: 2}', '天津', '天津市', '北辰区', 'zsb@hebut.edu.cn', '天津市北辰区西平道5340号', '300401', 'https://zs.hebut.edu.cn/', '022-60438029,022-60438259,022-60200832,022-60200835', '022-60438000', '河北工业大学的前身是创办于1903年的北洋工艺学堂，是我国最早的培养工业人才的高等学校之一，创办了全国最早的高校校办工厂。1929年改称河北省立工业学院，1995年更名为河北工业大学。学校1996年跻身国家首批“211工程”重点建设高校行列；2014年由河北省、天津市和教育部共建；2017年，入选国家“双一流”建设高校。118年来，学校始终秉承“兴工报国”办学传统和“勤', '26', '11', '2', '230', 103, '99', '138', '98', '1618', '94.59', '31.39', '3.13', '1597', '2303', '59.00', '41.00', '2', '1');
INSERT INTO `school` VALUES ('40', '中央音乐学院', '教育部', '53万', '1', '11', '1949', '82', '', '普通本科', '艺术类', '普通本科', '公办', '双一流', '{}', '北京', '北京市', '西城区', 'zhaoban@ccom.edu.cn', '北京市西城区鲍家街43号', '100031', 'http://zhaoban.ccom.edu.cn/login;JSESSIONID=54901193046a4433b0ff48dffa685a63', '010-66425504,010-66425818', '', '中央音乐学院1949年由中央人民政府政务院命名成立，是由1938年成立的延安鲁迅艺术学院音乐系、1940年建立的重庆国立音乐院、1948年成立的华北大学三部音乐系、1918年成立的国立北平艺术专科学校音乐系等几所音乐教育机构合并组建而成,是一所培养高级专门音乐人才的高等学府。1960年被国务院定为全国重点高等学校，2000年被列入国家“211工程”建设院校，是全国艺术院校中', '1', '1', '2', '1', 0, '175', '284', '0', '0', '98.84', '34.39', '17.34', '215', '131', '37.89', '62.11', '2', '1');
INSERT INTO `school` VALUES ('39', '北京外国语大学', '教育部', '131万', '1', '11', '1941', '770', '', '普通本科', '语言类', '普通本科', '公办', '双一流', '{A+: 30, B-: 1, C+: 3}', '北京', '北京市', '海淀区', 'bwzsb@bfsu.edu.cn', '北京市海淀区西三环北路2号', '100089', 'http://joinus.bfsu.edu.cn', '010-88816254,010-88816481', '', '北京外国语大学坐落在北京市海淀区西三环北路，在三环路两侧分设东、西两个校区，是教育部直属、首批“211工程”高校、“985”优势学科创新平台高校、首批“双一流”建设高校。东院校门更新北外是中国共产党创办的第一所外国语高等学校，前身是1941年成立于延安的中国抗日军政大学三分校俄文大队，后发展为延安外国语学校，建校始隶属于党中央领导。新中国成立后', '11', '2', '7', '1', 0, '69', '183', '45', '0', '94.71', '18.80', '28.51', '', '', '', '', '2', '1');
INSERT INTO `school` VALUES ('38', '北京交通大学', '教育部', '185万', '2', '11', '1896', '1000', '', '普通本科', '理工类', '普通本科', '公办', '双一流', '{A-: 7, B: 1, B+: 9, B-: 7, C: 4, C+: 1, C-: 3}', '北京', '北京市', '海淀区', '', '北京市海淀区西直门外上园村3号', '100044', 'https://zsw.bjtu.edu.cn', '010-51688441', '', '北京交通大学是教育部直属，教育部、交通运输部、北京市人民政府和中国国家铁路集团有限公司共建的全国重点大学，是国家“211工程”“985工程优势学科创新平台”“双一流”建设高校。北京交通大学作为交通大学的三个源头之一，历史渊源可追溯到1896年，前身是清政府创办的铁路管理传习所，是中国第一所专门培养管理人才的高等学校，是中国近代铁路管理、电信教育', '33', '21', '5', '296', 40, '41', '65', '29', '596', '98.72', '39.26', '15.54', '1405', '1960', '58.25', '41.75', '2', '1');
INSERT INTO `school` VALUES ('37', '西北大学', '陕西省', '232万', '3', '61', '1902', '2360', '国立西北大学', '普通本科', '综合类', '普通本科', '公办', '双一流', '{A+: 1, B: 13, B+: 8, B-: 1, C: 3, C+: 11, C-: 2}', '陕西', '西安市', '碑林区', 'zsb@nwu.edu.cn', '长安校区：陕西省西安市长安区郭杜教育科技产业区学府大道1号,太白校区：陕西省西安市太白北路229号,桃园校区：陕西省西安市高新四路155号', '710069', 'http://zsb.nwu.edu.cn/', '029-88302211', '', '西北大学肇始于1902年的陕西大学堂和京师大学堂速成科仕学馆。1912年始称西北大学。1923年改为国立西北大学。1937年西迁来陕的国立北平大学、北平师范大学、北洋工学院和北平研究院等组成国立西安临时大学，1938年改为国立西北联合大学，1939年复称国立西北大学。新中国成立后为教育部直属综合大学。1950年复名西北大学。1958年改隶陕西省主管。1978年被确定为全国重点大', '36', '24', '9', '34', 67, '52', '74', '56', '817', '89.33', '36.55', '0.00', '2008', '1468', '42.23', '57.77', '2', '1');
