INSERT INTO `school` VALUES ('34', '哈尔滨工业大学', '工业和信息化部', '271万', '15', '23', '1920', '5212', '', '普通本科', '理工类', '普通本科', '公办', '双一流', '{A: 11, A+: 3, A-: 12, B: 5, B+: 11, B-: 3, C: 2, C+: 3}', '黑龙江', '哈尔滨市', '南岗区', 'hitzsb@hit.edu.cn', '黑龙江省哈尔滨市南岗区西大直街92号', '150001', 'https://zsb.hit.edu.cn/', '0451-86414671,0451-86414771', '', '哈尔滨工业大学（简称哈工大）隶属于工业和信息化部，以理工为主，理工管文经法艺等多学科协调发展，拥有哈尔滨、威海、深圳三个校区。学校始建于1920年，1951年被确定为全国学习国外高等教育办学模式的两所样板大学之一，1954年进入国家首批重点建设的6所高校行列，曾被誉为工程师的摇篮。学校于1996年进入国家“211工程”首批重点建设高校，1999年被确定为国家首批', '42', '28', '21', '133', 13, '17', '12', '10', '203', '95.47', '50.20', '13.79', '1008', '2966', '74.64', '25.36', '1', '1');
INSERT INTO `school` VALUES ('33', '大连海事大学', '交通运输部', '118万', '21', '21', '1909', '2040', '大连海运学院', '普通本科', '理工类', '普通本科', '公办', '双一流', '{B: 2, B+: 1, B-: 6, C: 3, C+: 2, C-: 4}', '辽宁', '大连市', '甘井子区', 'bkzs@dlmu.edu.cn', '辽宁省大连市凌海路1号', '116026', 'http://bkzs.dlmu.edu.cn', '0411-84727233,0411-84724303', '', '大连海事大学（原大连海运学院）是交通运输部所属的全国重点大学，是国家“211工程”重点建设高校、国家“双一流”建设高校，是交通运输部、教育部、国家海洋局、国家国防科技工业局、辽宁省人民政府、大连市人民政府共建高校。学校素有“航海家的摇篮”之称，是中国著名的高等航海学府，是被国际海事组织认定的世界上少数几所“享有国际盛誉”的海事院校之一', '21', '9', '2', '2', 110, '119', '167', '0', '1434', '96.54', '27.91', '7.60', '1169', '2786', '70.44', '29.56', '2', '1');
